import { Component } from 'react';
import Particles from 'react-particles-js';
import './App.css';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import SignIn from './components/SignIn/SignIn';
import Register from './components/Register/Register';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Clarifai from 'clarifai';

const app = new Clarifai.App({
  apiKey: '39d501b8315c4bb68ddbb970a33971df'
 });

const particlesOptions = {
  particles: {
    number: {
      value: 80,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}
class App extends Component {
  constructor(){
    super();
    this.state={
      input:'',
      imageURL:'',
      box:{},
      route:'signin',
      isSigndeIn:false,
      user:{
            id:'',
            name:'',
            email:'',
            enteries:0,
            joined:new Date()
      }
    }
  }
  loadUser=(data)=>{
    this.setState({
      user:{
            id:data.id,
            name:data.name,
            email:data.email,
            enteries:data.enteries,
            joined:data.joined
          }
  })
  }

  calculateFaceLocation=(data)=>{
    const clarifaiFace=data.outputs[0].data.regions[0].region_info.bounding_box;
    const image=document.getElementById('inputimage');
    const width=Number(image.width);
    const height=Number(image.height);
    return {
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height),
    }
  }
  displayFaceBox=(box)=>{
    console.log(box);
    this.setState({box:box})
  }

    onInputChange=(event)=>{
      this.setState({input:event.target.value})
    }

    onButtonSubmit=()=>{
      this.setState({imageURL: this.state.input});
      app.models
      .predict(Clarifai.FACE_DETECT_MODEL,this.state.input)
        .then((response)=>this.displayFaceBox(this.calculateFaceLocation(response)))
        .catch((err)=>console.log(err));
    }
    onRouteChange=(route)=>{
      if(route ==='signout'){
        this.setState({isSigndeIn:false})
      }
      else if(route==='home'){
        this.setState({isSigndeIn:true})
      }
      this.setState({route:route})
    }
    render(){
      const {isSigndeIn, imageURL,box,route }= this.state;
      return (
        <div className="App">
          <Particles className='particles'
                params={particlesOptions}
              />
          <Navigation isSignedIn={isSigndeIn} onRouteChange={this.onRouteChange}/>
          {route==='home'
          ?<div>
            <Logo/>
            <Rank name={this.state.user.name} entries={this.state.user.entries}/>
            <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>
            <FaceRecognition box={box} imageURL={imageURL}/>
          </div>
          :(
            route==='signin'
            ?<SignIn loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
            :<Register loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
          )
        }
        </div>
      );
  }
}

export default App;
